//#define DEF_COLUMN "columnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnn%d"
#define DEF_COLUMN "nn%d"
#define DEF_COLUMN_VALUE "aaaaaaaaa%d"
//#define DEF_COLUMN_VALUE "%d"

int g_KeyCount;
int g_ValueLen;

class TimeCounter {
  public:
    TimeCounter () {
      reset ();
    }
    ~TimeCounter () {}
    void reset () {
      gettimeofday (&_start, NULL);
    }
    int GetTimeMilliS () {
      return get_interval () / 1000;
    }
    int GetTimeMicroS () {
      return get_interval ();
    }
    int GetTimeS () {
      return get_interval () / 1000000;
    }
  private:
    int get_interval () {
      struct timeval now;
      gettimeofday (&now, NULL);
      return (now.tv_sec - _start.tv_sec) * 1000000
             + now.tv_usec - _start.tv_usec;
    }
    struct timeval _start;
};

int main (int argc, char *argv[]) {
  g_KeyCount = atoi (argv[1]);
  mem_db::MemDB loCa;
  loCa.Init ("/home/w/conf/mem_db/mem_db.conf");
  string lszTable = "UserInfo";
  string lszKey = "1";

  vector<string> loColumns;

  for (int i = 0; i < g_KeyCount; i++) {
    char lszColumn[200] = {'\0'};
    sprintf (lszColumn, DEF_COLUMN, i);
    loColumns.push_back (lszColumn);
  }

  vector<string> loValues;

  for (int i = 0; i < g_KeyCount; i++) {
    char lszvalue[200000] = {'\0'};
    sprintf (lszvalue, DEF_COLUMN_VALUE, i);
    loValues.push_back (lszvalue);
  }

  TimeCounter loTime;
  int rc;
  loTime.reset ();
  rc = loCa.PutRowWithColumns (lszTable, lszKey, loColumns, loValues);
  cout << "PutRowWithColumns Time(us)=" << loTime.GetTimeMicroS () << endl;

  if (MEM_DB_SUCCESS != rc) {
    cout << "PutRowWithColumns failed!" << endl;
  } else {
    cout << "PutRowWithColumns success!" << endl;
  }

  std::vector<HostInfo> servers;
  loCa.ListHosts (servers);
  int len = servers.size ();

  for (int i = 0; i < len; i++) {
    printf ("ip: %s\n", servers[i].ip_addr.c_str ());
  }



  vector<string> loGetValues;
  loTime.reset ();
  rc = loCa.GetRowWithColumns (lszTable, lszKey, loColumns, loGetValues);
  cout << "GetRowWithColumns Time(us)=" << loTime.GetTimeMicroS () << endl;

  if (MEM_DB_SUCCESS != rc) {
    cout << "GetRowWithColumns failed!" << endl;
  } else {
    cout << "GetRowWithColumns success!" << endl;
#if 0

    for (int i = 0; i < (int)loColumns.size () / 100; i++) {
      cout << loColumns[i].c_str () << "=" << loGetValues[i].c_str () << endl;
    }

#endif
  }

  return 0;
}

